    this.state = {
      products: ProductsArray,
      maxPrice: false
    };
  }

  formInput = (type, price) => {
    this.setState({
      type: type,
      price: price
    });
  };

  handleChange = (e) => {
    console.log(e.target, e.target.value)
    this.setState({
        maxPrice: e.target.value
    })
  }

