import React from "react";
import ProductsArray from "../products.json";

import Product from "../Components/Product";

class Products extends React.Component {
  constructor() {
    super();
    this.state = {
      products: ProductsArray,
      maxPrice: false,
      minPrice: false
    };
  }

//   formInput = (type, price) => {
//     this.setState({
//       type: type,
//       price: price
//     });
//   };

  handleChange = (e) => {
    console.log(e.target.name, e.target.value)
    this.setState({
        [e.target.name]: e.target.value
    })
  }

//   createProducts() {
//     //products=array
//     const products = this.state.products;
//     const price = this.state.price;
//     return products.map(product => {
//       return (
//         <div>
//           <div className="work" />
//           <div className="filter">
//             <Product product={product} />
//             {/* <Products
//                             series={this.state.series}
//                             type={this.state.type}
//                             onFormInput={this.handleFormInput}
//                           />
//                           <Products
//                             products={products}
//                             type={this.state.type}
//                             price={this.state.price}
//                           /> */}
//           </div>
//         </div>
//       );
//     });
//   }
  render() {
    const { products, maxPrice, minPrice } = this.state;

    console.log(products, maxPrice, minPrice);

    let filteredProducts = products

    // If Max Price, Remove Products that are above that Value
    if(maxPrice){
        filteredProducts = filteredProducts.filter(product => product.price <= parseInt(maxPrice));
    }

    return (
      <div>
        <div className="productsPage">
          <div className="container-fluid text-center">
            <div className="row">
              <div className="col">
                <h2>THE STASH</h2>
                <p className="ShopMens">Shop Men's </p>
              </div>
            </div>
            <div className="row">
              {/* Price Input */}
              <div className="price-filter">
                <label>Price Filter</label>
                <input
                  type="number"
                  className="form-control form-control-sm"
                  name="maxPrice"
                  value={maxPrice}
                  placeholder="Maximum Price"
                  onChange={this.handleChange}
                />
                                <input
                  type="number"
                  className="form-control form-control-sm"
                  name="minPrice"
                  value={minPrice}
                  placeholder="Minimum Price"
                  onChange={this.handleChange}
                />
              </div>
              {/* {this.createProducts()} */}
              {filteredProducts.map((product, index) => {
                return (
                  <div key={index}>
                    <div className="work" />
                    <div className="filter">
                      <Product product={product} />
                      {/* <Products
                            series={this.state.series}
                            type={this.state.type}
                            onFormInput={this.handleFormInput}
                          />
                          <Products
                            products={products}
                            type={this.state.type}
                            price={this.state.price}
                          /> */}
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
export default Products;
