import React from 'react';
import '../styles.css';
import Footer from '../Components/Footer';
import Product from '../products.json'
import Button from 'react-bootstrap';
import Container from 'react-bootstrap/Container'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'


class Products extends React.Component {
    constructor() {
        super();
        this.state = {
            products: Product,
            price: 0,
        }
    }
    formInput = (type, price) => {
        this.setState ({
            type: type,
            price: price
        })
    }
    createProducts() {
        //products=array
        const products = this.state.products;
        const price = this.state.price;
        console.log(products);
        return products.map((product) => {
            console.log(product);
            return (
                <div>
                    <Row>
                    <div class="work">
                         <Col><div class="card" style={{width : '18rem'}}/></Col>
                            <div class="work-img">
                                <a href="ShopMens"><img class="card-img-top img-fluid" src={product.image} alt="pacsun huf shirt"/></a>
                                <div className="img-overlay"></div>
                            </div>
                            <div className="card-body">
                                <p>{product.name}</p>
                                <ul className="card-text">
                                    <li>{product.price}</li>
                                    <li>{product.description}</li>
                                    </ul>
                            </div>
                        </div>
                        </Row>
                </div>
            );
        })
    }
    render() {
        return (
            <div>
                <div class="productsPage">
                    <div class="container text-center">
                        <div class="row">
                            <div class="col">
                                <h2>THE STASH</h2>
                        </div>
                            </div>
                                <form>
                                    <label for="abvInput">Sort By Price</label>
                                    <input id="abvInput" type="checkbox" checked={this.props.price} ref="abvInput" onChange={this.handleChange} />
                                    <label for="seriesInput">Filter By Type</label>
                                    <select id="seriesInput" ref="seriesInput" onChange={this.handleChange}>
                                        <option value="t-shirt">T-SHIRTS</option>
                                        <option value="hoodie">HOODIES</option>
                                        <option value="jeans">JEANS</option>
                                        <option value="tops">TOPS</option>
                                        <option value="suit">SUITS</option>
                                        <option value="skirt">SKIRTS</option>
                                        <option value="shoes">SHOES</option>
                                        <option value="accessories">ACCESSORIES</option>
                                        <option value="skateboard">SKATEBOARDS</option>
                                        <option value="snowboard">SNOWBOARDS</option>
                                    </select>
                            </form>
                                <p className="ShopMens">Shop Men's </p>
                                {this.createProducts()}
                            </div>
                </div>
           </div>
        );
    }
}
export default Products;
