import React from "react";
import ProductsArray from "../products.json";

import Product from "../Components/Product";

class Products extends React.Component {
  constructor() {
    super();
    this.state = {
      products: ProductsArray,
      //not using at moment
      maxPrice: false,
      minPrice: false,
      filterType: "type"
    };
  }

  handleChange = (e) => {
    console.log(e.target.name, e.target.value)
    this.setState({
        [e.target.name]: e.target.value
    })
  }

  render() {
    const { products, maxPrice, minPrice, filterType } = this.state;

    console.log(products, maxPrice, minPrice, filterType);

    let filteredProducts = products

    // If Max Price, Remove Products that are above that Value
    if(maxPrice){
        filteredProducts = filteredProducts.filter(product => product.price <= parseInt(maxPrice));
    }

    return (
      <div>
        <div className="productsPage">
          <div className="container-fluid text-center">
            <div className="row">
              <div className="col-12">
                <h2>THE STASH</h2>
                <p className="ShopMens">Shop Men's </p>
              </div>
            </div>
            <div className="row">
              {/* Price Input */}
              <div className="filterHeader" style={{textAlign: 'center'}}>
              <div className="price-filter">
                <label>Price Filter</label>
                <input
                  type="number"
                  className="form-control form-control-sm"
                  name="maxPrice"
                  value={maxPrice}
                  placeholder="Maximum Price"
                  onChange={this.handleChange}
                />
                                <input
                  type="number"
                  className="form-control form-control-sm"
                  name="minPrice"
                  value={minPrice}
                  placeholder="Minimum Price"
                  onChange={this.handleChange}
                />

                  <h2>Filter by Type</h2>
                  <div>
                  <form>
                  <select className="filterType" onChange={this.handleChange} as="select">
                    <option value="All">All</option>
                    <option value="t-shirt">T-SHIRTS</option>
                    <option value="hoodie">HOODIES</option>
                    <option value="jeans">JEANS</option>
                    <option value="tops">TOPS</option>
                    <option value="suit">SUITS</option>
                    <option value="skirt">SKIRTS</option>
                    <option value="shoes">SHOES</option>
                    <option value="accessories">ACCESSORIES</option>
                    <option value="skateboard">SKATEBOARDS</option>
                    <option value="snowboard">SNOWBOARDS</option>
                  </select>
                  </form>
                  </div>
                  </div>
                  </div>
                  <div className="row col-12 ml-5">
                  {filteredProducts.map((product, index) => {
                    return (
                      <div key={index}>
                        <div className="work" />
                        <div className="filter">
                          <Product product={product} />
                        </div>
                      </div>
                    );
                  })}
                  </div>
              </div>
            </div>
          </div>
        </div>
    );
  }
}
export default Products;
