import React from 'react';

class Filter extends React.Component {
  state = {
    products: ProductsGroup.products,
    filterType: "All",
    filterPrice: false
  }
  handleChange = (e) => this.setState({
    [e.target.name]: e.target.value
  })
  filterProducts = (products, filterType, filterPrice) => {
    // shows products twice
    let productsFiltered = products;

    // Applies filters and Type
    if(filterType !== 'All'){
      productsFiltered = productsFiltered.filter(product => product.category.toLowerCase() === filterType.toLowerCase())
    }
    // Price
    if(filterPrice){
      productsFiltered = productsFiltered.filter(product => product.price <= parseInt(filterPrice));
    }
    // Return filtered products
    return productsFiltered
  }
  render() {
    const { products, filterType, filterPrice } = this.state;
    // Filters products
    const productsFiltered = this.filterProducts(products, filterType, filterPrice);
    return (
      <div className='wrapper'>
        {/* Filters */}
          <Form.Group>
            <Form.Label>Filter by Type</Form.Label>
            <Form.Control name="filterType" onChange={this.handleChange} as="select">
              <option value="All">All</option>
              <option value="t-shirt">T-SHIRTS</option>
              <option value="hoodie">HOODIES</option>
              <option value="jeans">JEANS</option>
              <option value="tops">TOPS</option>
              <option value="suit">SUITS</option>
              <option value="skirt">SKIRTS</option>
              <option value="shoes">SHOES</option>
              <option value="accessories">ACCESSORIES</option>
              <option value="skateboard">SKATEBOARDS</option>
              <option value="snowboard">SNOWBOARDS</option>
              </Form.Control>
          </Form.Group>
          <Form.Group>
            <Form.Label>Enter Your Maximum Price</Form.Label>
            <Form.Control name="filterPrice" type="number" onChange={this.handleChange}/>
          </Form.Group>
        <div className="product-grid">
          {productsFiltered.length > 1 ? (
            productsFiltered.map( (product, index) => <Product key={index} {...product} />)
          ) : (
            <p>Sorry, that product doesn't exist</p>
          )}
        </div>
      </div>
    );
  }
 }
 export default Filter;
