    constructor() {
        super();
        this.state = {
            products: ProductsArray,
            price: 0,
        }
    }
    formInput = (type, price) => {
        this.setState ({
            type: type,
            price: price
        })
    }
    createProducts() {
        //products=array
        const products = this.state.products;
        const price = this.state.price;
        console.log(products);
        return products.map((product) => {
            console.log(product);
            return (
                <div>
                    <div class="work">

